$(document).ready(function(){$.get("https://covid-19.bklyn.dev/cases",function(res){var data=res.data,$template=$(".covid-tile");$.each(data,function(i){var dateTime,$tile=$($template).clone(),count=data[i].numConfirmedCases.toString().replace(/\B(?=(\d{3})+(?!\d))/g,",");null!=typeof count&&0!=count||(count="OOPS!"),dateTime=-1==data[i].updatedAt.toString().search("1970-")?data[i].updatedAt.toString().split("+")[0].replace("T"," at "):"some time in the past, but with errors!",$tile.find(".covid-state").html(data[i].state),$tile.find(".covid-count").html(count),$tile.find(".covid-time").html("Last updated "+dateTime),$tile.find(".covid-url a").attr("href",data[i].url),$tile.insertAfter($template),$tile.css("display","block")})})});